function NodeCRUDcomponent ({command}) {

    if ({command} === "create"){
        return (
            <p>CREATE</p>
        );
    }
    if ({command} === "read"){
        return (
            <p>READ</p>
        );
    }
    if ({command} === "update"){
        return (
            <p>UPDATE</p>
        );
    }
    if ({command} === "delete"){
        return (
            <p>DELETE</p>
        );
    }


}

export default NodeCRUDComponent;
