import React from "react";

const NodeCRUDcomponent = ({command}) => {

    if ({command} === "create"){
        return (
            <div className = "node">
            <p>create</p>
        </div>
        );
    }

    if ({command} === "update"){
        return (
            <div className = "node">
            <p>update</p>
        </div>
        );
    }

    if ({command} === "delete"){
        return (
            <div className = "node">
            <p>delete</p>
        </div>
        );
    }

}

export default NodeCRUDcomponent;
