import React, {useState} from "react";
import NodeUpdate from "./nodeCRUDcommands/nodeUpdate";
import NodeRead from "./nodeCRUDcommands/nodeRead";


const NodeEdit = ({n}) => {
    const [active, setActive] = useState("Read")

    return ( 
        <div>
        <p>NODE EDIT</p>

        {active === "Read" && <NodeRead/>}
        {active === "Update" && <NodeUpdate/>}

        <button onClick={()=> setActive("Update")}>Node Update</button>
        <button>Node Delete</button>




        </div>
    );
}

export default NodeEdit;
