import React, { useRef, useEffect, useState, } from "react";
import ForceGraph2D from "react-force-graph-2d";
import NodeRead from "./nodeCRUDcommands/nodeRead";
import LinkRead from "./linkCRUDcommands/linkRead";
import NodeUpdate from "./nodeCRUDcommands/nodeUpdate";

import LinkUpdate from "./linkCRUDcommands/linkUpdate";


function  nodeHoverFunc(n) {

