import React, {useState } from "react";
import NodeUpdate from "./nodeUpdate";


const NodeRead = ({n}) => {
    const [active, setActive] = useState("")

    return ( 
        <div>
        <p>NODE READ {n.id}</p>


        <button onClick={()=> setActive("Update")}>Node Update</button>
        <button>Node Delete</button>


        {active === "Create" && <NodeUpdate/>}

        </div>
    );
}

export default NodeRead;
