import React, {useState } from "react";
import LinkUpdate from "./linkUpdate";


const LinkRead = ({n}) => {
    const [active, setActive] = useState("")

    return ( 
        <div>
        <p>Link READ</p>


        <button onClick={()=> setActive("Update")}>Link Update</button>
        <button>Link Delete</button>


        {active === "Create" && <LinkUpdate/>}

        </div>
    );
}

export default LinkRead;
