import React, {useState} from "react";
import httpClient from "./httpClient.js";
import GraphComponent from "./graphComponent.js";

const data = "";

function assignDataGlobal(respData){
    data = respData;
}




const FilterComponent = () => {
    const [query, setQuery] = useState("");

    return ( 

    <div>
        <h1 class="font-weight-light">Query Page</h1>
        <label>Input Query: </label>
        <input type = "text" value={query} onChange={(e)=> setQuery(e.target.value)}/>
        <button type="button" onClick={() => executeQuery(query)}>Submit</button>
        </div>
    );
}


const executeQuery = async (query) => {
    console.log(query);

    try {
        const dbData = await httpClient.post("//localhost:5000/query", {
            query
        });

        console.log("HI" + dbData.data)

        console.log(typeof(dbData.data))
        assignDataGlobal(dbData)
        //data = dbData.data;
        console.log(data)
        //HERE IS THE DATA

    } catch (error) {
        if (error.response.status === 401) {
            alert("Invalid Query");
        }
    }
};

const Query = () => {

    var data;

    const executeQuery = async (query) => {
        console.log(query);

        try {
            const dbData = await httpClient.post("//localhost:5000/query", {
                query
            });

            console.log("HI" + dbData.data)

            console.log(typeof(dbData.data))
            assignDataGlobal(dbData)
            data = "hi"
            console.log(data)

            //HERE IS THE DATA

        } catch (error) {
            if (error.response.status === 401) {
                alert("Invalid Query");
            }
        }
    };

    const FilterComponent = () => {
        const [query, setQuery] = useState("");

        return (
        <div>
            <h1 class="font-weight-light">Query Page</h1>
            <label>Input Query: </label>
            <input type = "text" value={query} onChange={(e)=> setQuery(e.target.value)}/>
            <button type="button" onClick={() => executeQuery(query)}>Submit</button>
            </div>
        );
    }

    return ( 
        <div class="container">

            <div class="row col-lg-6">
            <FilterComponent/>

            </div>
            <div class="row col-lg-6">

            </div>


            <div class="row align-items-center" >
            <GraphComponent input = {data}/>
            </div>


            <div class="row align-items-center my-5"/>

            <p className="filer-section">

            </p>

        </div>


);
}

export default Query;
